mapper.insert:
DECLARE
	SEQVALUE NUMBER;
BEGIN
	SELECT POST_ID_SEQ.NEXTVAL INTO SEQVALUE FROM DUAL;

	INSERT INTO POST
	VALUES(SEQVALUE, #{title}, #{item}, #{price}, #{deliveryTip});

	INSERT INTO LOCATION(ID, ADDR, MAPX, MAPY, REQUESTER_NAME, TYPE)
	VALUES(SEQVALUE, #{requesterLocation.addr}, #{requesterLocation.mapX}, #{requesterLocation.mapY}, #{requesterLocation.requesterName}, 1);

	INSERT INTO LOCATION(ID, ADDR, MAPX, MAPY, REQUESTER_NAME, MARKET_NAME, TYPE)
	VALUES(SEQVALUE, #{marketLocation.addr}, #{marketLocation.mapX}, #{marketLocation.mapY}, #{marketLocation.requesterName}, #{marketLocation.marketName}, 2);
END;
